-- Inscribir a todos los estudiantes e un curso
CREATE OR REPLACE FUNCTION Inscripcion_Estudiante()
RETURNS TRIGGER AS $$
BEGIN
    -- Asignar el estudiante a todos los mdulos del curso
    SELECT NEW.id_student, m.id
    FROM modules m
    WHERE m.id_course = NEW.id_course;
    -- Asignar el estudiante a todas las lecciones de los mdulos del curso
    SELECT NEW.id_student, l.id
    FROM lessons l
    JOIN modules m ON l.id_module = m.id
    WHERE m.id_course = NEW.id_course;
    -- Asignar el estudiante a todas las evaluaciones del curso
    SELECT NEW.id_student, e.id
    FROM evaluations e
    JOIN modules m ON e.id_module = m.id
    WHERE m.id_course = NEW.id_course;
    RETURN NEW;
END;
$$ LANGUAGE plpgsql;
-- DEFINIR EL TRIGGER
CREATE TRIGGER Inscripcion_Estudiante
AFTER INSERT ON students_courses 
FOR EACH ROW
EXECUTE FUNCTION Inscripcion_Estudiante();
