-- Creacion de la BD
CREATE DATABASE aprendemas;

-- SOLO PARA PSQL comentar si se va a ejecutar con PGADMIN u otro gestor de base de datos
\c aprendemas;

-- Creacion de la estructura (tablas)
USE DATABASE aprendemas;
-- Modulo de Gestion de cursos
-- Categoria de cursos
CREATE TABLE course_category (
  id INT GENERATED ALWAYS AS IDENTITY PRIMARY KEY,
  category VARCHAR(100)
);
-- Niveles de los cursos
CREATE TABLE course_level (
  id INT GENERATED ALWAYS AS IDENTITY PRIMARY KEY,
  level VARCHAR(12)
);
-- Estado de los cursos
CREATE TABLE course_status (
  id INT GENERATED ALWAYS AS IDENTITY PRIMARY KEY,
  status VARCHAR(8)
);
-- Tabla de Cursos
CREATE TABLE courses (
  id INT GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY,
  name VARCHAR(255) NOT NULL,
  description TEXT,
  category INT REFERENCES course_category(id),
  level INT REFERENCES course_level(id),
  duration INT,
  price NUMERIC(10, 2) NOT NULL,
  status INT REFERENCES course_status(id)
);
-- Modulos de los cursos
CREATE TABLE modules(
  id INT GENERATED ALWAYS AS IDENTITY PRIMARY KEY,
  id_course INT REFERENCES courses(id),
  title VARCHAR(100),
  description TEXT
);
-- Evaluaciones de los modulos
CREATE TABLE evaluations (
  id INT GENERATED ALWAYS AS IDENTITY PRIMARY KEY,
  id_module INT REFERENCES modules(id),
  max_grade NUMERIC(4,2) NOT NULL CHECK (max_grade > 0 and max_grade <= 20),
  title VARCHAR(100)
);
-- Lecciones de los modulos
CREATE TABLE lessons (
  id INT GENERATED ALWAYS AS IDENTITY PRIMARY KEY,
  id_module INT REFERENCES modules(id),
  title VARCHAR(100),
  duration INT
);

-- GESTION DE USUARIOS
-- Tipos de usuarios (perfiles)
CREATE TABLE user_types (
  id INT GENERATED ALWAYS AS IDENTITY PRIMARY KEY,
  type VARCHAR(12)
);
-- Tabla de control de usuarios
CREATE TABLE users (
  id INT GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY, 
  name VARCHAR(255) NOT NULL,
  lastname VARCHAR(255) NOT NULL,
  mail VARCHAR(255) UNIQUE NOT NULL,
  register_date TIMESTAMP DEFAULT CURRENT_TIMESTAMP,
  password VARCHAR(255) NOT NULL, 
  user_type INT REFERENCES user_types(id) NOT NULL
);
-- Insignias de los usuarios
CREATE TABLE badges (
  id INT GENERATED ALWAYS AS IDENTITY PRIMARY KEY,
  badge VARCHAR(255)
);
-- Especialidades de los instructores
CREATE TABLE specialties (
  id INT GENERATED ALWAYS AS IDENTITY PRIMARY KEY,
  specialty VARCHAR(255)
);
-- Tabla de estudiantes, relacionada a ID usuario, la logica del aplicativo debe discriminar que los usuarios referenciados sean de tipo Estudiante
CREATE TABLE students (
  id INT PRIMARY KEY REFERENCES users(id) ON DELETE CASCADE,
  points INT,
  badge INT REFERENCES badges(id)
);

-- Tabla de Instrucrores, relacionada a ID instructores, la logica del aplicativo debe discriminar que los usuarios referenciados sean de tipo Instrucrores
CREATE TABLE instructors (
  id INT PRIMARY KEY REFERENCES users(id) ON DELETE CASCADE,
  specialty INT REFERENCES specialties(id) NOT NULL,
  badge INT REFERENCES badges(id)
);
-- Tabla relacion entre instructores y cursos, N:M
CREATE TABLE instructors_courses (
  id_instructor INT REFERENCES instructors(id),
  id_course INT REFERENCES courses(id),
  PRIMARY KEY (id_instructor, id_course)
);
-- Tabla relacion entre estudiantes y cursos, N:M contiene informacion adicional sobre el progreso y el certificado
-- La logica para marcar el check del certificado se debe implementar en el aplicativo, segun la politica que tenga la plataforma
-- Es decir la capa de aplicacion esta encargada de discriminar si genera certificado basado en la data de las notas obtenidas o simplemente el progreso
CREATE TABLE students_courses (
  id_student INT REFERENCES students(id),
  id_course INT REFERENCES courses(id),
  inscription_date TIMESTAMP DEFAULT CURRENT_TIMESTAMP,
  progress INT CHECK (progress >= 0 and progress <= 100),
  grade INT CHECK (grade >= 0 and grade <= 20),
  cert BOOLEAN DEFAULT FALSE,
  PRIMARY KEY (id_student, id_course)
);
-- 4. SEGUIMIENTO DEL PROGRESO DEL ESTUDIANTE
-- Tabla relacion entre modulos y estudiantes util para medir el progreso
CREATE TABLE students_modules(
  id_student INT REFERENCES students(id),
  id_module INT REFERENCES modules(id),
  completed BOOLEAN DEFAULT FALSE,
  PRIMARY KEY (id_student, id_module)
);
-- Mismo caso que el anterior pero con las lecciones
CREATE TABLE students_lessons(
  id_student INT REFERENCES students(id),
  id_lesson INT REFERENCES lessons(id),
  completed BOOLEAN DEFAULT FALSE,
  PRIMARY KEY (id_student, id_lesson)
);
-- 5. EVALUACIONES Y CALIFICACIONES
-- Tabla de calificaciones, se tiene una verificacion para que las notas ingresadas no sean mayor que 20 o menor a 0, tomando en cuenta que el sistema calificativo
-- es basado a 20 pts
CREATE TABLE students_evaluations(
  id_student INT REFERENCES students(id),
  id_evaluation INT REFERENCES evaluations(id),
  grade INT CHECK(grade >= 0 and grade <= 20),
  PRIMARY KEY (id_student, id_evaluation)
);
-- 7. GESTION DE CONTENIDO EDUCATIVO
-- Tabla de tipos de contenido EDUCATIVO, videos, guias, etc..
CREATE TABLE content_type (
  id INT GENERATED ALWAYS AS IDENTITY PRIMARY KEY,
  type VARCHAR(100)
);
-- Tabla de registro de contenido, el campo url refiere a la ubicacion del contenido tanto interno como externo
CREATE TABLE content (
  id INT GENERATED ALWAYS AS IDENTITY PRIMARY KEY,
  type INT REFERENCES content_type(id),
  url TEXT UNIQUE NOT NULL
);
-- Relacion entre el contenido y las lecciones, un mismo contenido puede estar en multiples lecciones y una leccion puede tener multples contenidos asi que N:M
CREATE TABLE content_lessons (
  id_content INT REFERENCES content(id),
  id_lesson INT REFERENCES lessons(id),
  PRIMARY KEY (id_content, id_lesson)
);

