--- Generar data de progreso y calificaicones de los estudiantes

-- 1. Actualizar el progreso de los estudiantes en los módulos (marcar el 80% como completados)
UPDATE students_modules
SET completed = TRUE
WHERE (id_student, id_module) IN (
    SELECT
        sm.id_student,
        sm.id_module
    FROM students_modules sm
    JOIN modules m ON sm.id_module = m.id
    JOIN students_courses sc ON sm.id_student = sc.id_student AND m.id_course = sc.id_course
    WHERE RANDOM() < 0.8 -- Marcar aproximadamente el 80% de los módulos como completados
);

-- 2. Actualizar el progreso de los estudiantes en las lecciones (marcar el 80% como completadas)
UPDATE students_lessons
SET completed = TRUE
WHERE (id_student, id_lesson) IN (
    SELECT
        sl.id_student,
        sl.id_lesson
    FROM students_lessons sl
    JOIN lessons l ON sl.id_lesson = l.id
    JOIN modules m ON l.id_module = m.id
    JOIN students_courses sc ON sl.id_student = sc.id_student AND m.id_course = sc.id_course
    WHERE RANDOM() < 0.8 -- Marcar aproximadamente el 80% de las lecciones como completadas
);

-- 3. Actualizar las calificaciones de los estudiantes en las evaluaciones (solo para módulos completados)
UPDATE students_evaluations
SET grade = FLOOR(RANDOM() * 21) -- Genera una calificación aleatoria entre 0 y 20
WHERE (id_student, id_evaluation) IN (
    SELECT
        se.id_student,
        se.id_evaluation
    FROM students_evaluations se
    JOIN evaluations e ON se.id_evaluation = e.id
    JOIN modules m ON e.id_module = m.id
    JOIN students_modules sm ON se.id_student = sm.id_student AND m.id = sm.id_module
    WHERE sm.completed = TRUE -- Solo para módulos completados
);


